import {ACTION_TYPES} from "../actions/types";


const cartInitialState = [];


export default function cardReducer(state = cartInitialState, action){
    switch (action.type) {
        case ACTION_TYPES.ADD_CART:

            return state.map(el => {
                return el.id !== action.payload.id  && el
            })

            return [
                action.payload,
                ...state
            ]

            // return state.map(eachProd=>{
            //     eachProd.id !== action.payload.id ? [...state, action.payload]: state
            // })

        case ACTION_TYPES.REMOVE_CART:
            break;
        default:
            return state
    }
}
