import React from "react";
import {useSelector} from "react-redux";
export default function Cart(){

    const cart = useSelector(state => state.cart)

    return <ul>
        {cart.map(eachProd=>{
        return <li>{eachProd.name} <button style={{backgroundColor: "red", color: "#FFF", marginLeft: "20px"}}>Remove</button></li>
        })}
    </ul>
}
